import React from 'react'
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

const ProductsCarousel = ({ carouselData }) => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };
  return (
    <>
    <div className='container py-10'>
      {carouselData && carouselData.length && <Carousel
        showDots={false}
        responsive={responsive}
        infinite={true}
        autoPlay={false}
        autoPlaySpeed={1000}
        arrows={false}>
        {carouselData.map(item => {
          return <>
            <div className="d-flex flex-row border-5 border-amber-900 rounded-xl m-3 p-3">
              <img
                className="img-fluid h-40 w-40 px-2"
                src={item.imagePath}
                alt={item.title}
              />
              <div className='m-auto mx-0 px-2'>
                <div className='text-3xl font-bold text-amber-900 py-2'>{item.title}</div>
                <div className='text-black'>{item.subTitle}</div>
              </div>
            </div>
          </>
        })}
      </Carousel>}
      </div>
    </>
  );
};

export default ProductsCarousel;
