import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addCart } from "../../redux/action";
import filters from "../../data/productFilter"
import { Link } from "react-router-dom";

const Products = (props) => {
  const [data, setData] = useState(props.productsData);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product))
  }


  const filterProduct = (category) => {
    const updatedList = data.filter((item) => item.category === category);
    setData(updatedList);
  }
  return (
    <>
      <div className="container my-3 py-3">
        <div className="row">
          <div className="col-12">
            <h2 className="display-5 text-center">{props.title? props.title : <>Latest Products</>}</h2>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {props.showFilter && filters && filters.length && 
          <div className="buttons text-center py-5">
            {filters.map(item=>{
              return(
                        <button className="btn btn-outline-dark btn-sm m-2" onClick={() => filterProduct(item?.category)}>{item.title}</button>
              )
            })}
        </div>}


        {data?.map((product) => {
          return (
            <div id={product.id} key={product.id} className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
              <div className="card text-center h-100" key={product.id}>
                <img
                  className="card-img-top p-3"
                  src={product.image}
                  alt={product.title}
                  height={300}
                />
                <div className="card-body">
                  <h5 className="card-title">
                    {product.title.substring(0, 12)}...
                  </h5>
                  <p className="card-text">
                    {product.description.substring(0, 90)}...
                  </p>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item lead">RS {product.price}</li>
                </ul>
                <div className="card-body">
                  <button className="btn btn-dark m-1" onClick={() => addProduct(product)}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>

          );
        })}
        </div>
      </div>
    </>
  );
};

export default Products;
