import * as React from 'react'

const BannerCard = ({cardData}) => {
  return (
    <> 
       <div className="card text-white border-0 mx-3 d-flex bg-dark">
          <img
            className="card-img img-fluid h-40 w-40"
            src={cardData.imagePath}
            alt="Card"
            height={100}
            width={100}
          />
          <div className='d-flex flex-row'>
          <div className='font-bold	text-amber-900'>{cardData.title}</div>
          <div className='text-black'>{cardData.subTitle}</div>
          </div>
        </div>
    </>
  );
};

export default BannerCard;
