import React from 'react'
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

const ProductsCarousel = ({ carouselData }) => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };
  return (
    <>
      {carouselData && carouselData.length && <Carousel
        showDots={false}
        responsive={responsive}
        infinite={true}
        autoPlay={true}
        autoPlaySpeed={1000}
        arrows={false}>
        {carouselData.map(item => {
          return <>
            <div className="card border-0 d-flex flex-row rounded-xl w-auto md:w-1/5 mx-auto mx-lg-0 px-3 px-sm-4 px-md-5 px-lg-3 py-3 py-lg-4 container">
              <img
                className="card-img img-fluid h-40 w-40 px-2 border-1"
                src={item.imagePath}
                alt={item.title}
              />
              <div className='m-auto mx-0 px-2'>
                <div className='text-3xl font-bold text-amber-900 py-2'>{item.title}</div>
                <div className='text-black'>{item.subTitle}</div>
              </div>
            </div>
          </>
        })}
      </Carousel>}

    </>
  );
};

export default ProductsCarousel;
